import React, { useEffect, useRef, useState } from 'react';
import { useDispatchCart,useCart } from './ContextReducer';
export default function Card(props) {
let dispatch=useDispatchCart();
let data=useCart();
const priceRef=useRef();

  let option = props.options;
  let priceOptions=Object.keys(option);
  const [qty,setqty]= useState(1)
  const [size,setsize]=useState("")
const handletocart=async()=>{
  await dispatch({type:"ADD",id:props.foodItem.name,price:props.finalPrice,qty:qty,size:size});
  console.log(data);
}

let finalPrice=qty*parseInt(option[size]);
useEffect(()=>{
  setsize(priceRef.current.value)
},[])

  return (
    <div>
      <div>
        <div className="card mt-3" style={{ width: "18rem" ,"maxHeight":"400px"}}>
          <img className="card-img-top" src={props.foodItem.img} alt="..." style={{height:"120px",objectFit:"fill"}} />
          <div className="card-body">
          <h5 className='card-text'>{props.foodItem.name}</h5>
            <div className="container w-100">
                <select className="m-2 h-100  bg-success rounded">
                    {Array.from(Array(6),(e,i)=>{
                        return (
                            <option key={i+1} value={i+1}> {i+1} </option>
                        )
                    })}
                </select>

                <select className="m-2 h-100  bg-success rounded" ref={priceRef} onChange={(e)=>setqty(e.target.value)}>
                    {priceOptions.map((data)=>{
                      return <option key={data} value={data}>{data}</option>
                    })}
                </select>
                <div className="d-inline h-100 fs-5">
                â‚¹{finalPrice}/-
                </div>
                <hr></hr>
                <button className={`btn btn-success justify-center ms-2`} onClick={handletocart}>Add to Cart</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
